INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5816, 0, 'Ibandronate', 'Food significantly decreases the bioavailability of oral ibandronate.', 'food', 'Moderate', 'Oral ibandronate should be administered on an empty stomach (after an overnight fast of at least 6 hours) with a 180 to 240 mL glass of plain water and no other food or drink should be taken for at least 30 minutes after administration.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5817, 30261, 'Acalabrutinib', 'Grapefruit juice may increase the plasma concentrations of acalabrutinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.  Inhibition of hepatic CYP450 3A4 may also contribute.  The interaction has not been studied with grapefruit juice, but has been reported for other CYP450 3A4 inhibitors.', 'grapefruit juice', 'Major', 'Acalabrutinib may be administered with or without food.  Patients should avoid consumption of grapefruit and grapefruit juice during treatment with acalabrutinib.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5818, 20724, 'Ibrutinib', 'Coadministration with grapefruit, grapefruit juice, or Seville oranges may significantly increase the plasma concentrations of ibrutinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Major', 'Patients treated with ibrutinib should avoid consumption of Seville oranges, grapefruit, grapefruit juice, and any supplement containing grapefruit extract.  Ibrutinib should be taken once daily at approximately the same time each day.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5819, 20725, 'Ibrutinib', 'Coadministration with grapefruit, grapefruit juice, or Seville oranges may significantly increase the plasma concentrations of ibrutinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Major', 'Patients treated with ibrutinib should avoid consumption of Seville oranges, grapefruit, grapefruit juice, and any supplement containing grapefruit extract.  Ibrutinib should be taken once daily at approximately the same time each day.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5820, 10785, 'Hyoscyamine', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous.  In addition, the potential for abuse may be increased with the combination.  The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'alcohol', 'Moderate', 'Alcohol should generally be avoided during therapy with anticholinergic agents.  Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5821, 429, 'Ibuprofen', 'The concurrent use of aspirin or nonsteroidal anti-inflammatory drugs (NSAIDs) and ethanol may lead to gastrointestinal (GI) blood loss.  The mechanism may be due to a combined local effect as well as inhibition of prostaglandins leading to decreased integrity of the GI lining.', 'alcohol', 'Moderate', 'Patients should be counseled on this potential interaction and advised to refrain from alcohol consumption while taking aspirin or NSAIDs.', '', '', 'DDInter', 0);
