INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (347, 'Alkanazole 150mg 1 caps.', 'الكانازول 150 مجم 1 كبسولة', '26', '17.64', 'Antifungals.triazoles', 'Fluconazole', 'Future pharmaceutical industries (fpi) > international trading office', 'Cap', 'كبسولة', '150mg', '1', '', 'about fluconazole a triazole derivative a board range antifungal. mechanism of action of fluconazole fluconazole is fungicidal or fungistatic depending on the drug concentrations. i t inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhi', '', '', 1860, '2024-11-10', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Fluconazole, commonly known as _Diflucan_, is an antifungal drug used for the treatment of both systemic and superficial fungal infections in a variety of tissues.  It was initially approved by the FDA in 1990. This drug is an _azole_ antifungal, in the same drug family as [ketoconazole] and [itraconazole]. Fluconazole has many advantages over the other antifungal drugs including the option of oral administration. The side effect profile of this drug is minimal. It has been demonstrated as an efficacious treatment for vaginal yeast infections in one single dose.[A174325]', '["J01RA07", "J02AC01", "D01AC15"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (349, 'Kliedoflash 30 tabs', 'كليدوفلاش 30 قرص', '85', '', '', '', 'Organix', 'Tab', 'أقراص', '', '3', 'دعم قوة وسلامة العظام ويحمي من الهشاشة واللين. ويدعم قدرة الجهاز المناعي ويحسن حالة الشعر ويقوي الأظافر. يحافظ على الخلايا العصبية ويعزز المزاج والذاكرة. يقوي عضلة القلب ويعتبر مناسبًا في حالات سوء التغذية.', '', '', '', 2678, '2023-07-09', 0, NULL, NULL, NULL, NULL, 0, 0, 0, NULL, NULL, NULL);
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (351, 'Diclopro k 50 mg 10 sachet', 'ديكلوبرو كيه 50مجم 10 اكياس', '50', '35', 'Nsaid.acetic acid derivatives', 'Diclofenac potassium', 'Atco pharma > xeedia pharma', 'Sachet', 'أكياس', '50 mg', '1', '', 'about diclofenac potassium nsaid anti-inflammatory antipyretic and analgesic. mechanism of action of diclofenac potassium diclofenac possess analgesic anti-inflammatory and antipyretic action. it inhibits the enzyme cyclo-oxygenase and there by inhibits t', '6224010509108', '', 1290, '2024-10-28', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Diclofenac is a phenylacetic acid derivative and non-steroidal anti-inflammatory drug (NSAID).[label] NSAIDs inhibit cyclooxygenase (COX)-1 and-2 which are the enzyme responsible for producing prostaglandins (PGs). PGs contribute to inflammation and pain signalling. Diclofenac, like other NSAIDs, is often used as first line therapy for acute and chronic pain and inflammation from a variety of causes. Diclofenac was the product of rational drug design based on the structures of [phenylbutazone], [mefenamic acid], and [indomethacin].[A180796] The addition of two chlorine groups in the ortho position of the phenyl ring locks the ring in maximal torsion which appears to be related to increased potency. It is often used in combination with [misoprostol] to prevent NSAID-induced gastric ulcers. Diclofenac was first approved by the FDA in July 1988 under the trade name Voltaren, marketed by Novartis (previously Ciba-Geigy).[L7360]', '["S01CC01", "S01BC03", "M01AB55", "M02AA15", "M01AB05", "D11AX18"]', '[]');
