INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (525, 'Amigrawest 2.5mg 4 f.c. tab', 'اميجراويست 2.5 مجم 4 اقراص', '56', '39', 'Antimigraine.vasoconstrictor agents.selective serotonin agonist', 'Zolmitriptan', 'Western pharmaceuticals industries', 'Tab', 'أقراص', '2.5mg', '1', 'لعلاج حالات الصداع النصفي', '', '6224007308325', '', 6219, '2024-11-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Zolmitriptan is a member of the triptan class of 5-hydroxytryptamine(5-HT)1B/1D/(1F)receptor agonists used to treat acute migraine.[A462, L12978] [Sumatriptan] was the first triptan to be developed, but had poor oral bioavailability and lipophilicity. This led to the development of second-generation triptans, including [almotriptan], [eletriptan], [frovatriptan], [naratriptan], [rizatriptan], and zolmitriptan.[A193791] Triptans can be administered alone or in combination with an NSAID like [naproxen], and represent the current "gold standard" for acute migraine treatment.[A193797]

Zolmitriptan was first approved by the FDA for sale by Zeneca Pharmaceuticals under the trade name Zomig® on November 25, 1997. It is currently available in both tablet and nasal spray forms.[L12978]', '["N02CC03"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (527, 'Amikacin amoun 100mg/2ml vial', 'اميكاسين امون حقن 100مجم/2مل فيال', '5', '', 'Aminoglycoside antibiotic', 'Amikacin', 'Amoun', 'Vial', 'فيال', '100mg', '1', '', 'description amikacin is a semi-synthetic aminoglycoside antibiotic derived from kanamycin a. similar to other aminoglycosides amikacin disrupts bacterial protein synthesis by binding to the 30s ribosome of susceptible organisms. binding interferes with mr', '6221025019172', '', 1248, '2022-06-20', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Amikacin is a semi-synthetic aminoglycoside antibiotic that is derived from kanamycin A.[FDA label] Amikacin is synthesized by acylation with the l-(-)-γ-amino-α-hydroxybutyryl side chain at the C-1 amino group of the deoxystreptamine moiety of kanamycin A.[A39531]

Amikacin''s unique property is that it exerts activity against more resistant gram-negative bacilli such as Acinetobacter baumanii and Pseudomonas aeruginosa. Amikacin also exerts excellent activity against most aerobic gram-negative bacilli from the Enterobacteriaceae family, including Nocardia and some Mycobacterium (M. avium-intracellulare, M. chelonae, and M. fortuitum)[L4680]. M. avium-intracellulare (MAC) is a type of nontuberculous mycobacteria (NTM) found in water and soil. Symptoms of this disease include a persistent cough, fatigue, weight loss, night sweats, and shortness of breath and the coughing up of blood.[L4680]

Several forms of amikacin are used currently, including an intravenous (IV) or intramuscular (IM) injection.[F1949] In September 2018, a liposomal inhalation suspension of this drug was approved by the FDA for the treatment of lung disease caused by Mycobacterium avium complex (MAC) bacteria in a small population of patients with the disease who do not respond to traditional treatment.[L4680,L4681]', '["S01AA21", "D06AX12", "J01GB06", "J01RA06"]', '[]');
