INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (1420, 'Charclone 1000mg 30 tabs.', 'شاركلون 1000مجم 30 قرص', '195', '', 'Antiflatulent', 'Activated charcoal', 'Mira international', 'Tab', 'أقراص', '1000mg', '3', 'علاج الكحة', '', '6224009669448', '', 1340, '2024-07-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Activated charcoal, or activated carbon, is an amorphous form of carbon prepared from incomplete combustion of carbonaceous organic matter. It is activated by an oxidizing gas flow at high temperature passed over its surface to make a fine network of pores, producing a material with large surface area and high affinity for various substances. It is used as a gastric decontaminant and emergency medication to treat poisonings following excessive oral ingestion of certain medications or poisons by absorbing most drugs and toxins. However its effects is rendered poor on some compounds including strong acids or bases, methanol and substances with limited absorptive capacity (including iron, lithium, arsenic). It works by binding to the poison in the gastric contents in a reversible fashion thus may be adminstered together with a cathartic to reduce the small intestine transit time. The clinical applications of activated charcoal occured in the early 1800''s. While this management for acute poisoning is considered fairly invasive, it is on the World Health Organization''s List of Essential Medicines that includes the most important medications needed in a basic health system.', '["A07BA51", "A07BA01"]', '[]');
