INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2521, 'Choletimb 10 mg 10 tab.', 'كوليتيمب 10اقراص', '14', '', 'Hypolipidemic agent', 'Ezetimibe', 'Marcyrl co.', 'Tab', 'أقراص', '10 mg', '1', '', '', '', '', 1260, '2022-09-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Ezetimibe is a lipid-lowering compound that inhibits intestinal cholesterol and phytosterol absorption. The discovery and research of this drug began in the early 1990s, after the intravenous administration of radiolabelled ezetimibe in rats revealed that it was being localized within enterocytes of the intestinal villi - this prompted studies investigating the effect of ezetimibe on intestinal cholesterol absorption.[A15202] Ezetimibe is used as an adjunctive therapy to a healthy diet to lower cholesterol levels in primary hyperlipidemia, mixed hyperlipidemia, homozygous familial hypercholesterolemia (HoFH), and homozygous sitosterolemia (phytosterolemia).[L11347]

Unlike other classes of cholesterol-reducing compounds including statins and bile acid sequestrants, ezetimibe has a distinct mechanism of action involving the sterol transporter Niemann-Pick C1-Like 1 (NPC1L1), and is unique in that it does not affect the absorption of fat-soluble nutrients such as fat-soluble vitamins, triglycerides, or bile acids.[A33313] In genetically NPC1L1-deficient mice, a 70% reduction in intestinal cholesterol absorption was seen, and these mice were insensitive to ezetimibe treatment - it was determined based on these findings that NPC1L1 plays an essential role in promoting intestinal cholesterol uptake via an ezetimibe-sensitive pathway.[A15202] By interfering with the intestinal uptake of cholesterol and phytosterols, ezetimibe reduces the delivery of intestinal cholesterol to the liver.[L11347]', '["C10BA02", "C10BA11", "C10BA10", "C10BA05", "C10BA12", "C10AX09", "C10BA06"]', '[]');
