INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2932, 'Rani-f 20mg 6 sachets', 'راني-ف 20مجم فوار 6 اكياس', '42', '27', 'Antacid.hyperacidity.peptic ulcer', 'Famotidine', 'Pharco', 'Sachet', 'أكياس', '20mg', '6', '1- حرقان المعدة والحموضة.', 'about famotidine guanidine:thiazole derivative a histamine h2-receptor antagonist anti-secretory antiulcer. mechanism of action of famotidine famotidine is a h2 (histamine) receptor antagonist. histamine is the most potent stimulus of acid secretion and a', '6221151015888', '', 10563, '2024-08-28', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Famotidine is a competitive histamine-2 (H2) receptor antagonist that works to inhibit gastric acid secretion. It is commonly used in gastrointestinal conditions related to acid secretion, such as gastric ulcers and gastroesophageal reflux disease (GERD), in adults and children.[L11166] Compared to other H2receptor antagonists, famotidine displays high selectivity towards this receptor; in a study consisting of healthy volunteers and patients with acid hypersecretory disease, famotidine was about 20 to 50 times more potent at inhibiting gastric acid secretion than [cimetidine] and eight times more potent than [ranitidine] on a weight basis.[A189462] Famotidine is used in various over-the-counter and off-label uses.[L11166] While oral formulations of famotidine are more commonly used, the intravenous solution of the drug is available for use in hospital settings.[L11142]', '["A02BA03", "A02BA53"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2933, 'Conistova 1mg 28 tablets', 'كونيستوفا 1مجم 28 قرص', '248', '148', 'Laxative', 'Prucalopride', 'Mash premier', 'Tablet', 'أقراص', '1mg', '4', '', '', '', '', 10272, '2025-02-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Prucalopride is a dihydrobenzofurancarboxamide derivative from the benzofurane family that selectively stimulates 5-HT4 receptors and thus, it presents enterokinetic properties.[A37348] The high selectivity of prucalopride allowed further development as it prevented the cardiac adverse reactions observed due to non-target effects of precedent therapies.[A40254] Prucalopride was developed by Shire Development LLC and approved for use in Europe in 2009,[A40250] in Canada on December 7, 2011 and by the FDA on December 17, 2018.[L4880]', '["A06AX05"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2934, 'Perdofenex 267mg 270 caps', 'بيردوفينيكس 267مجم 270 كبسولة', '19,543', '', 'Antifibrotic', 'Pirfenidone', 'Multicare', 'Cap', 'كبسولة', '267mg', '1', 'مضاد لتجلط الرئة', '', '', '', 12890, '2023-01-21', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Pirfenidone is a synthetic pyridone drug.[A251370] It is an antifibrotic agent with anti-inflammatory and antioxidant properties [A251370] that is used to treat idiopathic pulmonary fibrosis (IPF),[L26801] which is a chronic, progressive form of interstitial pneumonia.[A251370] While its mechanism of action is not yet fully understood, pirfenidone is proposed to primarily regulate tumor necrosis factor (TNF) pathways and modulate cellular oxidation.[A251365] The FDA first approved pirfenidone alongside [nintedanib] as one of the first drugs to treat IPF.[A251165]', '["L04AX05"]', '[]');
