INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3423, 'Depo-medrol 40mg/ml vial', 'ديبو-ميدرول 40مجم 1 فيال', '9', '', 'Glucocorticoid', 'Methylprednisolone', 'Eipico > pfizer', 'Vial', 'فيال', '40mg', '1', '', '', '6221032310583', '', 2426, '2022-12-04', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Methylprednisolone is a [prednisolone] derivative glucocorticoid with higher potency than [prednisone].[A188811] It was first described in the literature in the late 1950s.[A188811,A188814]

