INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3697, 'Diltiazem 90mg s.r. 10 cap.', 'ديلتيازيم 90مجم 10 كبسولات ممتد المفعول', '8', '', 'Antihypertensive.nonselective calcium channel blocker', 'Diltiazem', 'Eipico', 'Cap', 'كبسولة', '90mg', '1', '', 'about diltiazem a benzothiazepine derivative calcium channel blocker anti hypertensive anti-anginal class iv antiarrhythmic. mechanism of action of diltiazem diltiazem is a benzothiazepine; ca channel blocker. it exerts it`s antianginal antihypertensive a', '6221032131669', '', 1446, '2023-08-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Diltiazem is a benzothiazepine derivative with antihypertensive and vasodilating properties. Approved in 1982 by the FDA, it is a member of the non-dihydropyridine calcium channel blockers drug class. It works through various mechanisms of action, but it primarily works by inhibiting the calcium influx into cardiac and vascular smooth muscle during depolarization.[L10556] Compared to dihydropyridine drugs, such as [nifedipine], that preferentially act on vascular smooth muscle and [verapamil] that directly acts on the heart muscle, diltiazem displays an intermediate specificity to target both the cardiac and vascular smooth muscle.[T28] Being a potent vasodilator, diltiazem is used clinically as an antihypertensive, anti-arrhythmic, and as an anti-anginal agent [L6289] for the management of cardiovascular conditions such as hypertension, chronic stable angina, atrial fibrillation, atrial flutter. Apart from its main FDA-approved indications, diltiazem has also been used for numerous off-label indications, such as anal fissures (in topical formulations), migraine prophylaxis, pulmonary hypertension, and rest-related cramps in the lower extremities.[L6289] Typically available in extended-release oral and intravenous formulations, diltiazem is marketed under various brand names with Cardizem and Tiazac being the most common ones.', '["C08DB01", "C05AE03"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3698, 'Dimetrol-n 20 f.c.tab.', 'ديميترول ان 20 قرص', '19', '14', 'Luminal amoebicide', 'Diloxanide furoate+mebeverine+metronidazole', 'Eva pharma', 'Tab', 'أقراص', '', '2', 'علاج الطفيليات.', 'about diloxanide furoate dichloroacetamide derivative anti-protozoal luminal amebicides. mechanism of action of diloxanide furoate diloxanide furoate is a dicloroacetamide derivative which acts as a luminal amoebicide. it exerts its action by directly kil', '', '', 1336, '2024-12-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, NULL, 'P01AC01', NULL);
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3699, 'Hycyanaro 1.5 % eye drops 5 ml', 'هايسيانارو 1.5% قطرة عين 5 مل', '82', '', 'Antibiotic.eye infection.macrolide', 'Azithromycin', 'Talent pharma group', 'Drops', 'نقط', '1.5 %', '1', '', '', '6225000399235', '', 1835, '2022-08-16', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Azithromycin is a broad-spectrum macrolide antibiotic with a long half-life and a high degree of tissue penetration [A174172]. It was initially approved by the FDA in 1991 [A174175].

It is primarily used for the treatment of respiratory, enteric and genitourinary infections and may be used instead of other macrolides for some sexually transmitted and enteric infections. It is structurally related to erythromycin [A174169].

Azithromycin [9-deoxo-9a-aza-9a-methyl-9a-homoerythromycin] is a part of the _azalide_ subclass of macrolides, and contains a 15-membered ring, with a methyl-substituted nitrogen instead of a carbonyl group at the 9a position on the aglycone ring, which allows for the prevention of its metabolism. This differentiates azithromycin from other types of macrolides [A174175].

In March 2020, a small study was funded by the French government to investigate the treatment of COVID-19 with a combination of azithromycin and the anti-malaria drug [hydroxychloroquine]. The results were positive, all patients taking the combination were virologically cured within 6 days of treatment, however, larger studies are required.[A192546]', '["J01RA07", "S01AA26", "J01FA10"]', '[]');
