INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (5097, 'Foradil 12 mcg 30 caps.+inhaler', 'فوراديل 12مكجم 30 كبسولة + جهاز استنشاق', '280', '162', 'Bronchodilator.long acting beta 2 agonist', 'Formoterol fumarate', 'Novartis', 'Cap', 'كبسولة', '12 mcg', '3', 'موسع شعب', 'mechanism of action the pharmacologic effects of beta2-adrenoceptor agonist drugs including formoterol are at least in part attributable to stimulation of intracellular adenyl cyclase the enzyme that catalyzes the conversion of adenosine triphosphate (atp', '6223002640942', '', 28328, '2023-11-16', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Formoterol is an inhaled beta2-agonist used in the management of COPD and asthma that was first approved for use in the United States in 2001.[L10986] It acts on bronchial smooth muscle to dilate and relax airways, and is administered as a racemic mixture of its active (R;R)- and inactive (S;S)-enantiomers.[A189528] A major clinical advantage of formoterol over other inhaled beta-agonists is its rapid onset of action (2-3 minutes), which is at least as fast as [salbutamol], combined with a long duration of action (12 hours) - for this reason, treatment guidelines for asthma recommend its use as both a reliever and maintenance medication.[L11256] It is available as a single-entity product [L10986,L11223] and in several formulations in combination with both inhaled corticosteroids [L10995,L10619] and long-acting muscarinic antagonists.[L10992,L10989]', '["R03AL09", "R03AL05", "R03AK09", "R03AL10", "R03CC15", "R03AL11", "R03AL07", "R03AK08", "R03AC13", "R03AK07", "R03AK11"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (5099, 'Vizimpro 45 mg 30 tabs.', 'فيزيمبرو 45 مجم 30 قرص', '30,750', '', 'Antineoplastic - 2nd generation egfr tyrosine kinase inhibitor', 'Dacomitinib', 'Pfizer', 'Tab', 'أقراص', '45 mg', '1', '', '', '', '', 743, '2025-06-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Dacomitinib, designed as (2E)-N-16-4-(piperidin-1-yl) but-2-enamide, is an oral highly selective quinazalone part of the second-generation tyrosine kinase inhibitors which are characterized by the irreversible binding at the ATP domain of the epidermal growth factor receptor family kinase domains.[A40009]

Dacomitinib was developed by Pfizer Inc and approved by the FDA on September 27, 2018.[L4810] Some evidence in the literature suggests the therapeutic potential of dacomitinib in the epithelial ovarian cancer model[A39624], although further investigations are needed.', '["L01EB07"]', '[]');
