INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (5150, 'Zetazolex 4 mg 30 f.c. tabs', 'زيتازولكس 4 مجم 30 قرص', '167', '', 'Psychiatric.antipsychotics', 'Brexpiprazole', 'Zeta pharma', 'Tab', 'أقراص', '4 mg', '3', '', '', '', '', 905, '2025-06-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Brexpiprazole is a novel D2 dopamine and serotonin 1A partial agonist, called serotonin-dopamine activity modulator (SDAM), and a potent antagonist of serotonin 2A receptors, noradrenergic alpha 1B and 2C receptors. Brexpiprazole is approved for the treatment of schizophrenia, and as an adjunctive treatment for major depressive disorder (MDD). Although it failed Phase II clinical trials for ADHD, it has been designed to provide improved efficacy and tolerability (e.g., less akathisia, restlessness and/or insomnia) over established adjunctive treatments for major depressive disorder (MDD).', '["N05AX16"]', '[]');
