INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (5386, 'Gatilox 400mg 5 f.c. tab. (n/a)', 'جاتيلوكس 400مجم 5 اقراص', '30', '', 'Antibiotic.quinolone', 'Gatifloxacin', 'Epci', 'Tab', 'أقراص', '400mg', '1', '', 'uses: gatifloxacin is used to treat a variety of bacterial infections. it works by stopping the growth of bacteria. this medication belongs to a class of drugs called quinolone antibiotics. this antibiotic only treats bacterial infections. it will not wor', '', '', 871, '2022-12-08', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Gatifloxacin is an antibiotic agent and a member of the fourth-generation fluoroquinolone family. It works by inhibiting the bacterial enzymes DNA gyrase and topoisomerase IV. It was first introduced by Bristol-Myers Squibb in 1999 under the brand name Tequin® for the treatment of respiratory tract infections. Gatifloxacin is available as tablets and in various aqueous solutions for intravenous therapy. It is also available as eye drops under the brand name Zymar® marketed by Allergan.

The FDA withdrew its approval for the use of non-ophthalmic drug products containing gatifloxacin due to the high prevalence of gatifloxacin-associated dysglycemia adverse event reports and the high incidence of hyperglycemic and hypoglycemic episodes in patients taking gatifloxacin compared to those on macrolide antibiotics.[L43942,L44037]', '["S01AE06", "J01MA16"]', '[]');
