INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6693, 'Ixel 50mg 28 caps.', 'ايكسيل 50مجم 28 كبسولة', '80', '', 'Psychiatric.antidepressants', 'Milnacipran hydrochloride', 'Pierre - fabre france > ramco', 'Cap', 'كبسولة', '50mg', '2', '', '', '', '', 1123, '2022-08-01', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Milnacipran is a selective serotonin and norepinephrine reuptake inhibitor (SNRI) and like many agents in this category was originally developed for and continues to be approved and indicated for the treatment of depression [F3928, F3934, A175786, A175951]. Furthermore, in 2009 the US FDA approved milnacipran for the additional indication of treating fibromyalgia [F3925], although other regional regulatory authorities like the EMA, among others, have not yet approved the agent for such treatment, citing lack of robust evidence of efficacy, insufficient demonstration of maintenance of effect, and other concerns [F3928, F3934]. Nevertheless, milnacipran demonstrates a somewhat unique characteristic among SNRIs to elicit a relatively balanced reuptake inhibition of both serotonin and noradrenaline, with a somewhat increased preference for noradrenaline reuptake inhibition - which is potentially a point of interest given the plausible proposal that noradrenaline plays an important role in the mitigation of pain signals in the descending inhibitory pain pathways in the brain and spinal cord [A175759, A175843, A175846].

Moreover, recent research has shown that the levorotatory enantiomer of milnacipran, levomilnacipran, may have the capacity to inhibit the activity of beta-site amyloid precursor protein cleaving enzyme-1 (BACE-1), which has investigationally been associated with β-amyloid plaque formation - making the agent a possible course of treatment for Alzheimer''s disease [A175957].', '["N06AX17"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6694, 'I.z. globin 30 cap', 'اي زد جلوبين 30 كبسولة', '75', '', 'Anemia', 'Iron', 'Pharo pharma', 'Cap', 'كبسولة', '', '3', 'مكمل غذائي غني بالحديد', '', '6221094089021', '', 2630, '2022-12-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'A metallic element found in certain minerals, in nearly all soils, and in mineral waters. It is an essential constituent of hemoglobin, cytochrome, and other components of respiratory enzyme systems. Its chief functions are in the transport of oxygen to tissue (hemoglobin) and in cellular oxidation mechanisms. Depletion of iron stores may result in iron-deficiency anemia. Iron is used to build up the blood in anemia.', '["B03AE02", "A11AA01", "B03AE01", "B03AE04", "B03AE03"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6695, 'Loteranol 0.5% eye gel 5 gm', 'لوتيرانول 0.5% جل للعين 5 جم', '47', '41', 'Glucocorticoid', 'Loteprednol etabonate', 'Orchidia', 'Gel', 'جل', '0.5%', '1', '', '', '6223005444400', '', 2133, '2025-07-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Loteprednol Etabonate (LE) is a topical corticoid anti-inflammatory. It is used in ophthalmic solution for the treatment of steroid responsive inflammatory conditions of the eye such as allergic conjunctivitis, uveitis, acne rosacea, superficial punctate keratitis, herpes zoster keratitis, iritis, cyclitis, and selected infective conjunctivitides. As a nasal spray, it can be used for the treatment and management of seasonal allergic rhinitis.

Most prescription LE products, however, tend to be indicated for the treatment of post-operative inflammation and pain following ocular surgery [FDA Label]. A number of such new formulations that have been approved include Kala Pharmaceutical''s Inveltys - the first twice-daily (BID) ocular corticosteroid approved for this indication, designed specifically to enhance patient compliance and simplified dosing compared to all other similar ocular steroids that are dosed four times daily [L4545].

Moreover, LE was purposefully engineered to be a ''soft drug'', one that is designed to be active locally at the site of administration and then rapidly metabolized to inactive components after eliciting its actions at the desired location, thereby subsequently minimizing the chance for adverse effects [A38693].', '[]', '[]');
