INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6796, 'Katrex 0.8% syrup 15 ml', 'كاتركس شراب 15 مل', '23', '15', 'Anthelmintic', 'Levamisole', 'Kahira', 'Syrup', 'شراب', '0.8%', '1', '', 'about levamisole synthetic imidazothiazole derivative biological response modifier anthelmintic antineoplastic adjunct. mechanism of action of levamisole levamisole is the levo isomer of tetramisole which is used as an anthelminthic agent. it exerts its a', '6221068402962', '', 1524, '2025-08-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Levamisole is an antihelminthic drug that was commonly used for the treatment of parasitic, viral, and bacterial infections. [A178117] It was manufactured by _Janssen_ and first used in 1969 as an agent to treat worm infestations[A178123] Levamisole was approved by the FDA in 1990 as an adjuvant treatment for colon cancer.[L6232] Prior to this, levamisole was used as an antirheumatic therapy in the 1970s and 1980s for patients with rheumatoid arthritis.[A178123] 

Because of its immunomodulatory effects, this drug has been studied in the treatment of various immune-mediated diseases, with some studies showing positive results. [A178117] This drug has also been used in combination with other drugs for the treatment of various cancers. [A178117, A178123] 

