INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7048, 'Lactodel 2.5mg 20 tab', 'لاكتوديل 2.5مجم 20 قرص', '72', '44', 'Dopamine agonist', 'Bromocriptine', 'Amoun', 'Tab', 'أقراص', '2.5mg', '2', 'علاج مرض الشلل الرعاش -- علاج انقطاع الحيض -- علاج مرض ضخامة الاطراف -- علاج الفشل الكبدي', '', '6221025003614', '', 3936, '2024-12-08', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Bromocriptine mesylate is a semisynthetic ergot alkaloid derivative with potent dopaminergic activity. It inhibits prolactin secretion and may be used to treat dysfunctions associated with hyperprolactinemia. Bromocriptine is also indicated for the management of signs and symptoms of Parkinsonian Syndrome, as well as the treatment of acromegaly. Bromocriptine has been associated with pulmonary fibrosis, and can also cause sustained suppression of somatotropin (growth hormone) secretion in some patients with acromegaly.

In 1995, the FDA withdrew the approval of bromocriptine mesylate for the prevention of physiological lactation after finding that bromocriptine was not shown to be safe for use.[L43942,L43947] It continues to be used for the indications mentioned above.', '["N04BC01", "G02CB01"]', '[]');
