INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7201, 'Leponex 25mg 50 tab.', 'ليبونكس 25مجم 50 قرص', '68', '46', 'Psychiatric.antipsychotics', 'Clozapine', 'Novartis > mylan', 'Tab', 'أقراص', '25mg', '5', '', 'about clozapine dibenzodiazepine derivative atypical antipsychotic tranquilizer anti-psychotic. mechanism of action of clozapine clozapine binds to dopamine receptors. it has more affinity towards d4 receptor than other dopamine receptors i.e.d1 d2 d3 &d5', '', '', 1734, '2023-11-27', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'A tricyclic dibenzodiazepine, classified as an atypical antipsychotic agent. It binds several types of central nervous system receptors, and displays a unique pharmacological profile. Clozapine is a serotonin antagonist, with strong binding to 5-HT 2A/2C receptor subtype. It also displays strong affinity to several dopaminergic receptors, but shows only weak antagonism at the dopamine D2 receptor, a receptor commonly thought to modulate neuroleptic activity. Agranulocytosis is a major adverse effect associated with administration of this agent.', '["N05AH02"]', '[]');
