INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8372, 'Moxen 15 mg 20 tabs', 'موكسين 15مجم 20قرص', '46', '30', 'Nsaid.oxicam', 'Meloxicam', 'Egpi', 'Tab', 'أقراص', '15 mg', '2', 'مسكن مضاد التهاب مضاد روماتيزوم', '', '6223003930240', '', 2794, '2025-01-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Meloxicam is a nonsteroidal anti-inflammatory drug (NSAID) used to relieve various types of pain, including pain caused by musculoskeletal conditions, osteoarthritis, and rheumatoid arthritis.[A190189] With a longer half-life than most other NSAIDS, it is a favorable option for those who require once-daily dosing. Meloxicam is available in oral, transdermal, and intravenous formulations. It is a preferential COX-2 inhibitor, purportedly reducing the risk of adverse gastrointestinal tract effects, however, this is a topic of controversy.[A190198,A190201]', '["N01BB59", "M01AC56", "M01AC06"]', '[]');
