INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8487, 'Mycostatin 100 000 i.u./ml susp. 30ml', 'ميكوستاتين نقط معلق 30 مل', '9', '', 'Antifungal.polyene', 'Nystatin', 'Glaxo smithkline > smithkline beecham', 'Susp', 'معلق', '30ml', '1', '', 'about nystatin polyene antifungal. mechanism of action of nystatin nystatin is a fungicidal as well as a fungistatic drug. it has high affinity for ergosterol present in the fungal cell membrane and bind with it and they orients in such a way that micropo', '6224001007002', '', 4582, '2022-12-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Nystatin is a polyene antifungal drug that has broad-spectrum fungicidal and fungistatic activity against a number of yeasts and fungi, most notably _Candida_ species.[L10686] It is one of the most effective antifungal agents synthesized by bacteria, in this case a strain of _Streptomyces noursei_,[L10776] and is closely related to [amphotericin B], differing only slightly in structure.[A188562] Nystatin has a greater antifungal activity than amphotericin B - parenterally administered nystatin, however, is associated with significant toxicity and is not available in a formulation appropriate for systemic use.[A188562] As it undergoes very little absorption following oral or topical administration, nystatin''s efficacy is limited to the treatment/prevention of cutaneous, mucocutaneous, and gastrointestinal fungal infections.[L10686,L10728]', '["D01AA01", "G01AA01", "A07AA02", "G01AA51"]', '[]');
