INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (10788, 'Spectone 100mg 20 tab.', 'سبيكتون 100مجم 20 قرص', '90', '70', 'Diuretic.potassium-sparing diuretics', 'Spironolactone', 'Kahira', 'Tab', 'أقراص', '100mg', '2', 'مدر للبول', '', '6221068010983', '', 7546, '2025-06-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Spironolactone is a potassium sparing diuretic like [eplerenone] that competitively inhibits mineralocorticoid receptors in the distal convoluted tubule to promote sodium and water excretion and potassium retention.[A11837] Spironolactone was originally developed purely for this ability before other pharmacodynamic properties of the drug were discovered.[A11837,A178246] It is indicated to treat a number of conditions including heart failure, deem, hyperaldosteronism, adrenal hyperplasia, hypertension, and nephrotic syndrome.[Label] Off label uses of spironolactone involving its antiandrogenic activity include hirsutism, female pattern hair loss, and adult acne vulgaris.[A178135] Spironolactone is also frequently used in medical gender transition.[A178138]

Spironolactone was developed in 1957, marketed in 1959, and approved by the FDA on January 21, 1960.[A178243,L6187]', '["C03DA01"]', '[]');