INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (12369, 'Xilopred 16 mg 20 tabs.', 'زيلوبريد 16مجم 20 قرص', '74', '51', 'Glucocorticoid', 'Methylprednisolone', 'Penta pharma', 'Tab', 'أقراص', '16 mg', '2', '', '', '6224007738788', '', 3614, '2024-12-12', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Methylprednisolone is a [prednisolone] derivative glucocorticoid with higher potency than [prednisone].[A188811] It was first described in the literature in the late 1950s.[A188811,A188814]

Methylprednisolone was granted FDA approval on 24 October 1957.[L10785] In the outbreak of COVID-19, low dose methylprednisolone-based therapy was successful in treating COVID-19-associated pneumonia in one patient with long-term immunosuppression.[A192813] The efficacy of methylprednisolone in novel coronavirus pneumonia is being investigated further in clinical trials.[L12666]', '["D10AA02", "H02AB04", "H02BX01", "D07CA02", "S01CA08", "D07AA01"]', '[]');
