INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13308, 'Accofil 30 miu/0.5ml 5 prefilled syringes', 'اكوفيل 30مليون وحدة/0.5مل 5 سرنجات معبأة', '1,625', '', 'Granulocyte colony-stimulating factor (g-csf)', 'Filgrastim', 'Accord healthcare limited > united group pharmaceutical(u.g.p.)', 'Syringe', 'حقنة', '0.5ml', '5', 'عامل نمو مكون للدم -- يستخدم بعد عملية زرع نخاع العظام -- يستخدم بعد العلاج الكميائي', 'about filgrastim a cytokine glycoprotein a human granulocyte colony-stimulating factor (g-csf) analog immunostimulant. mechanism of action of filgrastim filgrastim is a cytokine glycoprotein that stimulates proliferation differentiation and functional act', '', '', 1070, '2022-10-04', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Filgrastim is a short-acting recombinant, non-pegylated human granulocyte colony-stimulating factor (G-CSF) analog produced by recombinant DNA technology. It has an amino acid sequence identical to endogenous G-CSF, but it is non-glycosylated unlike the endogenous G-CSF and has an N-terminal methionine added in the sequence for expression in _E. Coli_.[L40714] Human G-CSF is a glycoprotein that regulates the production and release of neutrophils from the bone marrow. Filgrastim mimics the biological actions of G-CSF to increase the levels of neutrophils in the blood.[L40719] It has a number of therapeutic uses, including the management and prevention of infections and febrile neutropenia in patients receiving myelosuppressive chemotherapy or radiation therapy. It is also used to manage severe chronic neutropenia and mobilize hematopoietic progenitor cells to the peripheral blood for collection by leukapheresis in patients undergoing peripheral blood progenitor cell collection and therapy.[L40714]

Filgrastim was approved in the US in 1991 and there are biosimilars available with similar therapeutic indications.[A245858] Tbo-filgrastim was approved by the FDA on August 29, 2012.[L36325] Filgrastim-sndz was approved on March 6, 2015 [L40768] and filgrastim-ayow was approved on March 2, 2022.[L40773] A long-acting, pegylated G-CSF, [pegfilgrastim], was made available to increase the duration of action of the drug.', '["L03AA02"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13309, 'Accolate 20mg 28 tab.', 'اكولات 20 مجم 28 قرص', '82', '', 'Leukotriene d4 and e4 antagonist', 'Zafirlukast', 'Astra zeneca', 'Tab', 'أقراص', '20mg', '4', '', 'indication for the prophylaxis and chronic treatment of asthma. mechanism of action zafirlukast is a selective and competitive receptor antagonist of leukotriene d4 and e4 (ltd4 and lte4) components of slow-reacting substance of anaphylaxis (srsa). cystei', '', '', 795, '2022-07-27', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Zafirlukast is an oral leukotriene receptor antagonist (LTRA) for the maintenance treatment of asthma, often used in conjunction with an inhaled steroid and/or long-acting bronchodilator. It is available as a tablet and is usually dosed twice daily. Another leukotriene receptor antagonist is montelukast (Singulair), which is usually taken just once daily.

Zafirlukast blocks the action of the cysteinyl leukotrienes on the CysLT1 receptors, thus reducing constriction of the airways, build-up of mucus in the lungs and inflammation of the breathing passages.', '["R03DC01"]', '[]');
