INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13411, 'Actimoxiflox 400 mg 5 f.c.tabs.', 'اكتيموكسيفلوكس 400 مجم 5 اقراص', '54', '', 'Antibiotic.quinolone', 'Moxifloxacin', 'International drug agency (idi) > mira international', 'Tab', 'أقراص', '400 mg', '1', '', 'about moxifloxacin a third generation synthetic fluoroquinolone antibiotic a broad spectrum antibiotic. mechanism of action of moxifloxacin moxifloxacin acts by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the', '', '', 1006, '2022-08-09', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Moxifloxacin is a synthetic fluoroquinolone antibiotic agent. Bayer AG developed the drug (initially called BAY 12-8039) and it is marketed worldwide (as the hydrochloride) under the brand name Avelox (in some countries also Avalox) for oral treatment.', '["S01AE07", "J01MA14"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13412, 'Activase 50mg 29miu (2vials)', 'اكتيفيز 50مجم 29 مليون وحدة دولية 2 فيال', '8,100', '', 'Tissue plasminogen activator', 'Alteplase', 'Genentech > egyptian pharmaceutical trading company', 'Vial', 'فيال', '50mg', '2', '', '', '', '', 1148, '2022-08-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Alteplase is a recombinant tissue plasminogen activator (rt-PA) used as a thrombolytic agent.[L43125] It cleaves plasminogen to form plasmin, an enzyme involved in the degradation of fibrin clots. In the absence of fibrin, the alteplase-mediated conversion of plasminogen is limited, thanks to the high affinity between alteplase and fibrin.[A252330,L43125] Alteplase is a purified glycoprotein of 527 amino acids expressed in Chinese hamster ovary (CHO) cells.[A252345,L43125] It was first approved by the FDA in 1987 for the management of thromboembolic disease, including acute myocardial infarction (AMI).[A252270] The use of alteplase to manage AMI has decreased thanks to the availability of safer treatments such as angioplasty and stenting. However, its use for the treatment of acute ischemic stroke (AIS) has increased over the years.[A252340] New thrombolytic agents derived from tissue plasminogen activator, such as [desmoteplase], [tenecteplase] and [reteplase], have also been developed.[A252270,A252345] Alteplase is also available as Cathflo Activase (intracatheter instillation) for the restoration of function to central venous access devices.[L34864]', '["B01AD02", "S01XA13"]', '[]');