INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13587, 'Agrelocit 50 mg 20 tab.', 'اجريلوسيت 50 مجم 20 قرص لعلاج الاكتئاب والتوتر', '35', '', 'Psychiatric.antidepressants', 'Sertraline', 'Memphis > atm pharmaceutical industries', 'Tab', 'أقراص', '50 mg', '2', '', 'about sertraline selective serotonin reuptake inhibitor (ssri) antidepressant antidepressant mechanism of action of sertraline sertraline blocks the re-uptake of neurotransmitter serotonin from the synapse in to the presynaptic nerve terminal in the cns.', '', '', 1013, '2022-07-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Sertraline is a popular antidepressant medication commonly known as a selective serotonin reuptake inhibitor (SSRI), and is similar to drugs such as [Citalopram] and [Fluoxetine]. Despite marked structural differences between compounds in this drug class, SSRIs exert similar pharmacological effects.

Several weeks of therapy with sertraline may be required before beneficial effects are noticed. Sertraline displays enhanced safety or tolerability than other classes of antidepressants, which frequently cause high levels of drowsiness, dizziness, blurred vision, and other undesirable effects.[A1846,A187075,T28]', '["N06AB06"]', '[]');
