INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13688, 'Alexcorda 200 mg 30 tablets', 'اليكسكوردا 200مجم 30 قرص', '96', '21', 'Antiarrhythmias.class iii', 'Amiodarone', 'Alexandria', 'Tablet', 'أقراص', '200 mg', '3', 'عدم انتظام ضربات القلب', 'description an antianginal and antiarrhythmic drug. it increases the duration of ventricular and atrial muscle action by inhibiting na k-activated myocardial adenosine triphosphatase. there is a resulting decrease in heart rate and in vascular resistance.', '', '', 2023, '2025-04-24', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Amiodarone is a benzofuran derivative, anti-arrhythmic drug used commonly in a variety of settings.[A36817] Most known for its approved indication in life-threatening ventricular arrhythmias, it is also used off-label in the outpatient and inpatient setting for atrial fibrillation. Because of its ability to cause serious toxicity and possibly death, amiodarone use should be reserved for its approved indications, according to prescribing information.[L3561,L11265,L11286]', '["C01BD01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13689, 'Alexgenta 0.1% topical cream 15 gm', 'اليكسجينتا 0.1% كريم موضعي 15 جم', '5', '', 'Aminoglycoside antibiotic', 'Gentamicin', 'Alexandria', 'Cream', 'كريم', '0.1%', '1', '', 'about gentamicin aminoglycoside antibiotic. mechanism of action of gentamicin gentamicin exerts its bactericidal action against gram negative organisms & some of gram positive organisms by inhibiting bacterial protein synthesis. the process involves 1. pe', '', '', 1044, '2022-07-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Gentamicin is a bactericidal aminoglycoside that was discovered and isolated from _Micromonospora purpurea_ in 1963.[A234349] It is one of the most frequently prescribed aminoglycosides due to its spectrum of activity, low cost, and availability.[A234339,A234354] Gentamicin is effective against both gram-positive and gram-negative organisms but is particularly useful for the treatment of severe gram-negative infections including those caused by _Pseudomonas aeruginosa_.[A233325,A234359,A234364] There is the added benefit of synergy when gentamicin is co-administered with other antibacterials such as beta-lactams.[A234364] This synergistic activity is not only important for the treatment of complex infections, but can also contribute to dose optimization and reduced adverse effects.[A234359,A234364]

Although gentamicin is well-established and may be used in a variety of clinical applications, it is also associated with severe adverse effects including nephrotoxicity and ototoxicity which may limit its use.[A234369]', '["S01AA11", "S02AA14", "D06AX07", "S03AA06", "J01GB03"]', '[]');
