INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14272, 'Anxbeat 15 mg 10 tab.', 'انكسبيت 15 مجم 10 اقراص', '8', '', 'Psychiatric.anxiolytics-hypnotics', 'Buspirone', 'Sigma', 'Tab', 'أقراص', '15 mg', '1', '', 'about buspirone psychotropic drug serotonin 5-ht1a receptor partial agonist weak dopamine (d2) antagonist azaspirodecanediones derivative sedative anxiolytic agent. mechanism of action of buspirone buspirone has a partial agonistic action on 5ht1a recepto', '', '', 893, '2022-07-12', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Buspirone is a novel anxiolytic agent with a unique structure and a pharmacological profile. Belonging to the azaspirodecanedione drug class,[A180991] buspirone is a serotonin 5-HT1Areceptor agonist that is not chemically or pharmacologically related to benzodiazepines, barbiturates, and other sedative/anxiolytic drugs.[L4478] Unlike many drugs used to treat anxiety, buspirone does not exhibit anticonvulsant, sedative, hypnotic, and muscle-relaxant properties. Due to these characteristics, buspirone been termed ''anxioselective''.[A180985] First synthesized in 1968 then patented in 1975,[L7375] it is commonly marketed under the brand name Buspar®. Buspirone was first approved in 1986 by the FDA [A181751] and has been used to treat anxiety disorders, such as generalized anxiety disorder (GAD), and relieve symptoms of anxiety. It has also been used as a second-line therapy for unipolar depression when the use of selective serotonin reuptake inhibitors (SSRIs) is deemed clinically inadequate or inappropriate.[L7375] The potential use of buspirone in combination with [melatonin] in depression and cognitive impairment via promoting neurogenesis has also been investigated.[A181751]', '["N05BE01"]', '[]');
