INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14666, 'Avazir 0.3% eye oint. 5 gm', 'افازير 0.3% مرهم للعين 5 جم', '26', '18', 'Aminoglycoside antibiotic', 'Tobramycin', 'Orchidia pharmaceutical industries', 'Oint', 'مرهم', '0.3%', '1', '', '', '6223005441621', '', 855, '2025-01-21', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Aminoglycosides, many of which are derived directly from _Streptomyces_ spp., are concentration-dependent bactericidal antibiotics with a broad spectrum of activity against Gram-positive and Gram-negative organisms.[A232294] Inhaled tobramycin is notable for its use in treating chronic _Pseudomonas aeruginosa_ infections in cystic fibrosis patients, as _P. aeruginosa_ is notoriously inherently resistant to many antibiotics.[A232294, A232299, L32739] However, tobramycin can also be administered intravenously and topically to treat a variety of infections caused by susceptible bacteria.[L32744, L32749] Its use is limited in some cases by characteristic toxicities such as nephrotoxicity and ototoxicity, yet it remains a valuable option in the face of growing resistance to front-line antibiotics such as β-lactams and cephalosporins.[A232294, A232349, L32739, L32749]

