INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14745, 'Averozolid 100mg/5ml susp. 150ml', 'افيروزوليد 100مجم/5مل معلق زجاجة 150 مل', '106', '74', 'Antibiotic.oxazolidinone', 'Linezolid', 'El-obour > averroes pharma-egypt', 'Susp', 'معلق', '100mg', '1', '', 'about linezolid oxazolidinone derivative reversible nonselective monoamine oxidase inhibitor antibiotic. mechanism of action of linezolid linezolid binds to the 23 s fraction of 50 s ribosome and inhibit the formation of 70s initiation complex. indication', '6224008179306', '', 1399, '2025-01-07', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Linezolid is a synthetic antibiotic which is used for the treatment of infections caused by aerobic Gram-positive bacteria. Its effects are bacteriostatic against both enterococci and staphylococci and bactericidal against most isolates of streptococci.[L32965] Linezolid exerts its antibacterial activity by inhibiting the initiation of bacterial protein synthesis - more specifically, it binds to the 23S ribosomal RNA of the 50S subunit[A11227,A199050] and, in doing so, prevents the formation of the 70S initiation complex which is essential for bacterial reproduction.

Linezolid was initially approved in 2000 and was the first member of the oxazolidinone antibiotic class.[A233425] A second member of this class, [tedizolid], was approved by the FDA in 2014 and is considered generally more effective and tolerable than its predecessor.', '["J01XX08"]', '[]');
