INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (15466, 'Bravelle 75 i.u. i.m./s.c. 10 vials', 'برافيل 75 وحدة دولية 10 فيال', '700', '', 'Infertility drugs.follicle stimulating hormone', 'Urofollitropin', 'Ferring pharmaceuticals > multipharma', 'Vial', 'فيال', '', '1', '', 'indication for treatment of female infertility pharmacodynamics used for the treatment of female infertility urofollitropin or follicle stimulating hormone (fsh) stimulates ovarian follicular growth in women who do not have primary ovarian failure. fsh th', '7640128540335', '', 1135, '2022-12-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Urofollitropin is a urinary-derived follicle-stimulating hormone (FSH) that is extracted and purified from human urine samples. It consists of two non-covalently linked, non-identical glycoproteins designated as the alpha- and beta- subunits. The alpha- and beta- subunits have 92 and 111 amino acids. The alpha subunit is glycosylated at Asn 51 and Asn 78 while the beta subunit is glycosylated at Asn 7 and Asn 24. Urofollitropin is typically used injected subcutaneously in combination with human chorionic gonadotropin (hCG) to induce ovulation. Urofollitropin may also be used to cause the ovary to produce several follicles, which can then be harvested for use in gamete intrafallopian transfer (GIFT) or in vitro fertilization (IVF).', '["G03GA04"]', '[]');
