INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (15928, 'Castor oil mepaco 60 ml', 'زيت خروع ميباكو 60 مل', '30', '', 'Laxative', 'Castor oil', 'Mepaco', 'Oil', 'زيت', '60 ml', '1', 'ملين -- علاج حالات الامساك الشديدة -- يستخدم كشربة لافراغ المعدة والامعاء من الفضلات لاجراء التشخيص او العمليات الجراحية', '', '6222018805079', '', 1755, '2022-12-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Castor oil is a vegetable oil obtained by pressing the seeds of the castor oil plant (_Ricinus communis_ L.) mainly cultivated in India, South America, Africa, and China. Castor oil is a rich source of [DB02955], which represents up to 90% of the total castor oil content. It also consists up to 4% linoleic, 3% oleic, 1% stearic, and less than 1% linolenic fatty acids [A31170]. [DB02955] has a hydroxyl group that provides a functional group location for various chemical reactions, making it a favourable substance in industrial applications [A31170]. Castor oil does not contain ricin, which is a natural poison found in the castor oil plant; the toxic lectin remains in the bean pulp following oil isolation [F70]. Due to its renewability and high versatility in addition to being the only commercial source of a hydroxylated fatty acid [A31170], castor oil has been used as a vital raw material for the chemical industry [A33045]. Castor oil was mainly used in the manufacturing of soaps, lubricants, and coatings [A31170]. It is an FDA-approved food additive directly added to food products for human consumption. It can also be found in hard candies as a release agent and anti-sticking agent, or supplementary vitamins and mineral oral tablets as an ingredient for protective coatings. Castor oil is found in over-the-counter oral liquids as a stimulant laxative, and is also added in commercial cosmetic, hair, and skincare products.', '["A06AB05"]', '[]');
