INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (16835, 'Cosentyx 150 mg/ml s.c. 2 prefilled pens', 'كوزينتكس 150 مجم/مل 2 اقلام معبأة للحقن', '13,360', '9582', 'Il-17a antagonist', 'Secukinumab', 'Novartis', 'Pen', 'قلم', '150 mg', '2', 'علاج الصدفية -- علاج الروماتيزم -- بعض الامراض المناعية', 'the first and only fully human il-17a antagonist. secukinumab is indicated for the treatment of moderate to severe plaque psoriasis in adult patients who are candidates for systemic therapy or phototherapy- and is indicated for the treatment of adult pati', '', '', 2934, '2024-05-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Secukinumab is a fully human monoclonal IgG1/κ antibody against interleukin-17A (IL-17A), a proinflammatory cytokine implicated in various chronic immune-mediated inflammatory disorders, such as plaque psoriasis.[L39600] By blocking the actions of IL-17A, secukinumab works to inhibit the pro-inflammatory pathways that drive immune-mediated inflammatory disorders.[A249840] Following its first global approval in Japan in December 2014, secukinumab was approved by the European Commission on January 15, 2015, and by the FDA a few days after (January 21, 2015).[A249805] It is currently approved to treat a number of chronic inflammatory conditions, such as plaque psoriasis, psoriatic arthritis, and ankylosing spondylitis.[L39600,L42280]', '["L04AC10"]', '[]');
