INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17018, 'Cyramza 100mg/10ml vial i.v. inf.', 'سيرامزا 100 مجم / 10 مل فيال', '15,534', '11400', 'Antineoplastic', 'Ramucirumab', 'Eli lilly', 'Vial', 'فيال', '100mg', '1', '', 'ramucirumab is a fully human monoclonal antibody (igg1) developed for the treatment of solid tumors. it is directed against the vascular endothelial growth factor receptor 2 (vegfr2). by binding to vegfr2 it works as a receptor antagonist blocking the bin', '', '', 661, '2024-11-10', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Ramucirumab is a human monoclonal antibody (IgG1) against vascular endothelial growth factor receptor 2 (VEGFR2), a type II trans-membrane tyrosine kinase receptor expressed on endothelial cells. By binding to VEGFR2, ramucirumab prevents binding of its ligands (VEGF-A, VEGF-C, and VEGF-D), thereby preventing VEGF-stimulated receptor phosphorylation and downstream ligand-induced proliferation, permeability, and migration of human endothelial cells. VEGFR stimulation also mediates downstream signalling required for angiogenesis and is postulated to be heavily involved in cancer progression, making it a highly likely drug target. In contrast to other agents directed against VEGFR-2, ramucirumab binds a specific epitope on the extracellular domain of VEGFR-2, thereby blocking all VEGF ligands from binding to it. Ramucirumab is indicated for us in advanced gastric or gastro-esophageal junction adenocarcinoma as a single agent or in combination with paclitaxel after prior fluoropyrimidine- or platinum-containing chemotherapy.', '["L01FG02"]', '[]');
