INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17824, 'Donifoxate 40 mg 30 f.c. tablets', 'دونيفوكسيت 40مجم 30 قرص', '102', '73.25', 'Antigout', 'Febuxostat', 'Eva pharma', 'Tablet', 'أقراص', '40 mg', '3', '', 'indication: treatment of hyperuricaemia in conditions where urate deposition has already occured. dose: 1 tablet once daily', '6224009763085', '', 1622, '2023-09-10', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Febuxostat is a non-purine xanthine oxidase (XO) inhibitor.[A39743] In early 2008, febuxostat was granted marketing authorization by the European Commission for the treatment of chronic hyperuricemia and gout.[A230548] In the following year, the FDA for approved febuxostat for use in the chronic management of hyperuricemia in adult patients with gout who have an inadequate response or intolerance to [allopurinol].[L32238] Gout is a form of arthritis that is caused by the accumulation of uric acid crystal in or around a joint, leading to inflammation and further deposition of uric acid crystal deposition in bones, joints, tissues, and other organs in the long term. Gout is closely associated with hyperuricemia. Febuxostat works by inhibiting the activity of an enzyme that is responsible for the synthesis of uric acid, thereby reducing serum uric acid levels.[A230548] 

