INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18080, 'Edoxagrel 15 mg 10 f.c. tabs.', 'ادوكساجريل 15 مجم 10 أقراص', '65', '', 'Antiplatlet.factor xa inhibitor', 'Edoxaban', 'Rameda', 'Tab', 'أقراص', '15 mg', '1', '', 'edoxaban is a direct and reversible inhibitor of activated factor x (factor xa) which prevents conversion of prothrombin to thrombin and prolongs clotting time thereby reducing the risk of thrombus formation. indications: prophylaxis of stroke and systemi', '', '', 821, '2022-07-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Edoxaban is a member of the Novel Oral Anti-Coagulants (NOACs) class of drugs, and is a rapidly acting, oral, selective factor Xa inhibitor. By inhibiting factor Xa, a key protein in the coagulation cascade, edoxaban prevents the stepwise amplification of protein factors needed to form blood clots. It is indicated to reduce the risk of stroke and systemic embolism (SE) in patients with nonvalvular atrial fibrillation (NVAF) and for the treatment of deep vein thrombosis (DVT) and pulmonary embolism (PE) following 5-10 days of initial therapy with a parenteral anticoagulant. Traditionally, warfarin, a vitamin K antagonist, was used for stroke prevention in these individuals but effective use of this drug is limited by it''s delayed onset, narrow therapeutic window, need for regular monitoring and INR testing, and numerous drug-drug and drug-food interactions. This has prompted enthusiasm for newer agents such as dabigatran, apixaban, and rivaroxaban for effective clot prevention. In addition to once daily dosing, the benefits over warfarin also include significant reductions in hemorrhagic stroke and GI bleeding, and improved compliance, which is beneficial as many patients will be on lifelong therapy.', '["B01AF03"]', '[]');
