INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18248, 'Emijectacin 100mg/2ml i.m./i.v. vial', 'ايميجيكتاسين 100مجم/2مل فيال عضل/وريد', '13', '5', 'Aminoglycoside antibiotic', 'Amikacin', 'Hikma specialized pharmaceuticals', 'Vial', 'فيال', '100mg', '1', 'مضاد حيوي', 'description amikacin is a semi-synthetic aminoglycoside antibiotic derived from kanamycin a. similar to other aminoglycosides amikacin disrupts bacterial protein synthesis by binding to the 30s ribosome of susceptible organisms. binding interferes with mr', '', '', 977, '2025-01-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Amikacin is a semi-synthetic aminoglycoside antibiotic that is derived from kanamycin A.[FDA label] Amikacin is synthesized by acylation with the l-(-)-γ-amino-α-hydroxybutyryl side chain at the C-1 amino group of the deoxystreptamine moiety of kanamycin A.[A39531]

Amikacin''s unique property is that it exerts activity against more resistant gram-negative bacilli such as Acinetobacter baumanii and Pseudomonas aeruginosa. Amikacin also exerts excellent activity against most aerobic gram-negative bacilli from the Enterobacteriaceae family, including Nocardia and some Mycobacterium (M. avium-intracellulare, M. chelonae, and M. fortuitum)[L4680]. M. avium-intracellulare (MAC) is a type of nontuberculous mycobacteria (NTM) found in water and soil. Symptoms of this disease include a persistent cough, fatigue, weight loss, night sweats, and shortness of breath and the coughing up of blood.[L4680]

Several forms of amikacin are used currently, including an intravenous (IV) or intramuscular (IM) injection.[F1949] In September 2018, a liposomal inhalation suspension of this drug was approved by the FDA for the treatment of lung disease caused by Mycobacterium avium complex (MAC) bacteria in a small population of patients with the disease who do not respond to traditional treatment.[L4680,L4681]', '["S01AA21", "D06AX12", "J01GB06", "J01RA06"]', '[]');
