INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18496, 'Eraloner 25 mg 20 f.c.tab.', 'ايرالونير 25مجم 20 قرص', '136', '102', 'Aldosterone receptor antagonist', 'Eplerenone', 'Multi-apex', 'Tab', 'أقراص', '25 mg', '2', 'علاج ارتفاع ضغط الدم ( قد يستخدم وحده أو مع غيره من أدوية ارتفاع ضغط الدم) قُصُورُ القلْبِ الاحْتِقانِي الذي يحدث بعد احتشاء عضلة القلب.', 'description eplerenone an aldosterone receptor antagonist. similar to spironolactone has been shown to produce sustained increases in plasma renin and serum aldosterone consistent with inhibition of the negative regulatory feedback of aldosterone on renin', '6223003205331', '', 10910, '2025-06-17', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Eplerenone, an aldosterone receptor antagonist similar to spironolactone, has been shown to produce sustained increases in plasma renin and serum aldosterone, consistent with inhibition of the negative regulatory feedback of aldosterone on renin secretion. The resulting increased plasma renin activity and aldosterone circulating levels do not overcome the effects of eplerenone. Eplerenone selectively binds to recombinant human mineralocorticoid receptors relative to its binding to recombinant human glucocorticoid, progesterone and androgen receptors.', '["C03DA04"]', '[]');
