INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (19115, 'Flazacor 30 mg 10 tabs.', 'فلازاكور 30مجم 10 اقراص', '148', '98', 'Glucocorticoid', 'Deflazacort', 'Sedico', 'Tab', 'أقراص', '30 mg', '1', 'كورتيزون يستخدم ك مضاد للالتهابات ومثبط للمناعة.', 'deflazacort is oral corticosteroid with anti-infalammatory and immunosuppresive effects. indications: -endocrine disorders. -rheumatic disorders. -collagen disease. -dermatologic diseases. -allergic diseases. -respiratory diseases. -ophthalmic disorders.', '6221042127010', '', 2114, '2025-01-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Deflazacort, also known as Emflaza, is a corticosteroid prodrug used as an agent to manage Duchenne Muscular Dystrophy (DMD). It is marketed by Marathon Pharmaceuticals and was approved in February 2017 by the FDA.[L6694,FDA label]

Duchenne Muscular Dystrophy is an inherited disorder resulting from mutations of the dystrophin gene, which is important for muscle function. This disease can cause serious muscle weakness and progressive breathing and cardiovascular disability, severely impacting patient quality of life and survival.[A179446,A179449,L6697] This disease usually manifests by muscle weakness in early childhood followed by loss of the ability to walk (ambulation) as early as age 7.[A179449]

Deflazacort delays the onset of muscle related complications resulting from DMD[A179455], prolonging the lives of children diagnosed with this disease and exerting less harmful effects on the bone health and weight than other steroid medications.[A179452,A25340]', '["H02AB13"]', '[]');
