INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (19365, 'Fosforon 3gm 1 sachet', 'فوسفورون 3جم 1 كيس', '39', '', 'Uti infection', 'Fosfomycin', 'Globe international pharmaceuticals', 'Sachet', 'أكياس', '3gm', '1', 'علاج العدوي في مجري البول', 'single-dose for treatment of urinary tract infections in adults.', '', '', 2715, '2022-05-16', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Fosfomycin was discovered in 1969 by scientists at the Spanish Penicillin and Antibiotics Company and is produced by _Streptomyces fradiae_.[A229338,A230348] It may also be produced synthetically and is commercially available as the disodium salt for intravenous administration and as the calcium or trometamol salt for oral administration.[A230348] In terms of chemical structure, fosfomycin is a phosphoenolpyruvate analog and contains a phosphonic group and an epoxide ring.[A230348]

Due to its ease of administration as a single 3-gram oral dose and desirable safety profile, fosfomycin has largely become a first-line therapeutic option for the treatment of uncomplicated urinary tract infections (UTIs) in females.[A230378] Despite being FDA approved only for urinary tract infections, fosfomycin actually has a broad spectrum of activity and is active against both gram-positive and gram-negative bacteria.[A230348] As such there is great interest in exploring the usefulness of fosfomycin for indications beyond the treatment of UTIs.[A230353,A230358]', '["J01XX01"]', '[]');
