INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (19542, 'Gadovist 1 mmol/ml (7.5ml) prefilled syringe(n/a yet)', 'جادوفيست 1 مليمول / مل (7.5 مل) حقنة مملوءة مسبقًا', '374', '', '', 'Gadobutrol', 'Bayer schering > bayer healthcare', 'Syringe', 'حقنة', '7.5ml', '1', '', '', '', '', 439, '2022-08-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Intravenous gadobutrol is a second-generation extracellular non-ionic macrocyclic GBCA (gadolinium-based contrast agent) used in magnetic resonance imaging (MRI) in adults and children older than 2 years of age. It may help visualize and detect vascular abnormalities in the blood brain barrier (BBB) and central nervous system (CNS). 

In patients with impaired renal function, gadolinium based contrast agents increase the risk of nephrogenic systemic fibrosis (NSF). A physician should be contacted if symptoms of NSF are encountered, such as dark or red patches on the skin; stiffness in joints; trouble moving, bending or straightening arms, hands, legs or feet; burning, itching, swelling, scaling, hardening and tightening of skin; pain in hip bones or ribs; or muscle weakness. 

Common adverse reactions that may be experienced include headache, nausea, feeling hot, abnormal taste, and warmth, burning or pain local to the injection site. 

General precautions should be taken in patients who are pregnant or breastfeeding, or who have a history of allergic reaction to contrast media, bronchial asthma or an allergic respiratory disorder.', '["V08CA09"]', '[]');
