INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (20922, 'Inzad 1 gm vial i.m./i.v.', 'انزاد 1 جم فيال', '34', '22.5', 'Antibiotic.cephalosporin.third-generation', 'Ceftazidime', 'Rameda > marcyrl co.', 'Vial', 'فيال', '1 gm', '1', '', 'about ceftazidime third generation cephalosporin antibiotic parenteral antibiotic. mechanism of action of ceftazidime ceftazidime is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms b', '6223003572990', '', 1162, '2025-06-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Bacteria possess a cell wall comprising a glycopeptide polymer commonly known as peptidoglycan, which is synthesized and remodelled through the action of a family of enzymes known as "penicillin-binding proteins" (PBPs).[A232920] β-lactam antibiotics, including cephalosporins, are PBP inhibitors that, through inhibition of essential PBPs, result in impaired cell wall homeostasis, loss of cell integrity, and ultimately bacterial cell death.[A232920, A232925, A232930] Ceftazidime is a third-generation cephalosporin with broad-spectrum antibacterial activity, including against some treatment-resistant bacteria such as _Pseudomonas aeruginosa_.[L32935]

