INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21692, 'Lemsisart 40mg 14 tabs.', 'لمسيسارت 40 مجم 14 قرص', '34', '', 'Antihypertensive.angiotensin blocker', 'Telmisartan', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '40mg', '2', '', 'about telmisartansangiotensin ii receptor antagonist antihypertensive.smechanism of action of telmisartansit is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive individuals. it bl', '', '', 846, '2022-07-24', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Telmisartan is an angiotensin II receptor antagonist (ARB) used in the management of hypertension. Generally, angiotensin II receptor blockers (ARBs) such as telmisartan bind to the angiotensin II type 1 (AT1) receptors with high affinity, causing inhibition of the action of angiotensin II on vascular smooth muscle, ultimately leading to a reduction in arterial blood pressure. Recent studies suggest that telmisartan may also have PPAR-gamma agonistic properties that could potentially confer beneficial metabolic effects.', '["C09DB04", "C09CA07", "C09DA07"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21693, 'Lemsisart 80mg 14 tabs.', 'لمسيسارت 80 مجم 14 قرص', '45', '', 'Antihypertensive.angiotensin blocker', 'Telmisartan', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '80mg', '2', '', 'about telmisartan angiotensin ii receptor antagonist antihypertensive. mechanism of action of telmisartan it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive individuals. it bl', '', '', 795, '2022-07-25', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Telmisartan is an angiotensin II receptor antagonist (ARB) used in the management of hypertension. Generally, angiotensin II receptor blockers (ARBs) such as telmisartan bind to the angiotensin II type 1 (AT1) receptors with high affinity, causing inhibition of the action of angiotensin II on vascular smooth muscle, ultimately leading to a reduction in arterial blood pressure. Recent studies suggest that telmisartan may also have PPAR-gamma agonistic properties that could potentially confer beneficial metabolic effects.', '["C09DB04", "C09CA07", "C09DA07"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21694, 'Lenalid 10mg 30 caps.(illegal import)', 'ليناليد 10مجم 30 كبسولة', '0', '', 'Immunosuppressants', 'Lenalidomide', 'Natco pharma limited >', 'Cap', 'كبسولة', '10mg', '3', 'مثبط للمناعة', 'thalidomide analog; immunomodulatory and antiangiogenic; inhibits secretion of proinflammatory cytokines; enhances cell-mediated immunity by stimulating proliferation of anti-cd3 stimulated t cells', '', '', 3702, '2023-01-08', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Lenalidomide (previously referred to as CC-5013) is an immunomodulatory drug with potent antineoplastic, anti-angiogenic, and anti-inflammatory properties.[A228553] It is a 4-amino-glutamyl analogue of [thalidomide] [A228543] and like thalidomide, lenalidomide exists as a racemic mixture of the active S(-) and R(+) forms.[A228708] However, lenalidomide is much safer and potent than thalidomide, with fewer adverse effects and toxicities.[A714, A228543] Thalidomide and its analogues, including lenalidomide, are referred to as immunomodulatory imide drugs (also known as cereblon modulators), which are a class of immunomodulatory drugs that contain an imide group. Lenalidomide works through various mechanisms of actions that promote malignant cell death and enhance host immunity.[A228708] Available as oral capsules, lenalidomide is approved by the FDA and EU for the treatment of multiple myeloma, myelodysplastic syndromes, mantle cell lymphoma, follicular lymphoma, and marginal zone lymphoma in selected patients.[L16028] Due to severe teratogenicity, pregnancy must be excluded before the start of treatment and patients must enroll in the lenalidomide Risk Evaluation and Mitigation Strategy (REMS) program to ensure contraception adherence.[A228708]', '["L04AX04"]', '[]');
