INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21834, 'Limitless kids multivitamins & iron 30 chocolate balls', 'ليميتلس كيدز فيتامينات متعددة وحديد 30 قطعة شيكولاتة', '95', '80', 'Iron supplement', 'Iron', 'Eva pharma', 'Unknown', 'غير محدد', '', '1', 'مصدر للحديد -- علاج الانيميا للاطفال', '', '6223012480033', '', 2136, '2023-09-05', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'A metallic element found in certain minerals, in nearly all soils, and in mineral waters. It is an essential constituent of hemoglobin, cytochrome, and other components of respiratory enzyme systems. Its chief functions are in the transport of oxygen to tissue (hemoglobin) and in cellular oxidation mechanisms. Depletion of iron stores may result in iron-deficiency anemia. Iron is used to build up the blood in anemia.', '["B03AE02", "A11AA01", "B03AE01", "B03AE04", "B03AE03"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21835, 'Limitless zinc 25 mg 30 tabs.', 'ليميتلس زنك 25 مجم 30 قرص', '89', '60', 'Zinc', 'Zinc gluconate', 'Eva pharma', 'Tab', 'أقراص', '25 mg', '1', 'مكمل غذائي -- مصدر للزنك --مقوي للمناعة', 'ï- supports a healthy immune system for all family.', '', '', 6257, '2023-08-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Zinc gluconate is a zinc salt of gluconic acid comprised of two gluconic acid molecules for each zinc cation (2+). Zinc gluconate is a generally recognized as safe (GRAS) substance by FDA [L2081]. It is available as a trace mineral supplement and over the counter as a lozenge form for a reduced duration of common colds and with decreased symptom severity.

Although it has been nasally administered for treating the common cold, this route of administration has been associated with some cases of anosmia [A32414], [A32409], [A32410], [L2080].

Studies show that zinc may be better absorbed in humans in the gluconate form [A32412], [L2105], however, results from other studies may vary.[A27280, L2082]

Interestingly, zinc supplementation has become a critical intervention for treating diarrheal episodes in children. Studies suggest that administration of zinc along with new low osmolarity oral rehydration solutions/salts (oral rehydration solution), may reduce both the duration and severity of diarrheal episodes for up to 12 weeks [L422].

More information about Zinc (in its natural form) is available at [DB01593].', '["C05AX04", "A12CB02"]', '[]');
