INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23595, 'Nerkardou 5 mg 30 orodispersible films', 'نيركاردو 5مجم 30 فيلم ذائب بالفم', '90', '60', 'Anti-hypertensive.beta blocker.selective b1', 'Bisoprolol fumarate', 'nerhadou international co.', 'Film', 'فيلم', '5 mg', '1', '', '', '6224011153331', '', 1549, '2024-08-14', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Bisoprolol is a cardioselective β1-adrenergic blocking agent used to treat high blood pressure.[A180472,L7219] It is considered a potent drug with a long-half life that can be used once daily to reduce the need for multiple doses of antihypertensive drugs.[A180472] Bisoprolol is generally well tolerated, likely due to its β1-adrenergic receptor selectivity and is a useful alternative to non-selective β-blocker drugs in the treatment of hypertension such as [Carvedilol] and [Labetalol]. It may be used alone or in combination with other drugs to manage hypertension[L7219] and can be useful in patients with chronic obstructive pulmonary disease (COPD) due to its receptor selectivity.[A180562]', '["C07FX04", "C07BB07", "C07AB07", "C09BX05", "C09BX02", "C09BX04", "C07FB07"]', '[]');
