INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23617, 'Nit clean 100mg/5ml pd. for oral susp. 60 ml', 'نت كلين 100مجم/5مل للاطفال معلق 60 مل', '32', '20.5', 'Antihelminthic', 'Nitazoxanide', 'Sedico > western pharmaceuticals industries', 'Susp', 'معلق', '100mg', '1', '', 'about nitazoxanide a synthetic nitrothiazolyl-salicylamide derivative antihelminthics in schistosomiasis. mechanism of action of nitazoxanide it excerts its action by blocking anaerobic energy metabolism. the drug interfering with the pyruvate-ferredoxin', '6224007308400', '', 1210, '2024-12-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Nitazoxanide belongs to the class of drugs known as _thiazolides_.  Nitazoxanide (NTZ) is a broad-spectrum anti-infective drug that markedly modulates the survival, growth, and proliferation of a range of extracellular and intracellular protozoa, helminths, anaerobic and microaerophilic bacteria, in addition to viruses. This drug is effective in the treatment of gastrointestinal infections including Cryptosporidium parvum or Giardia lamblia in healthy subjects. It is generally well tolerated. Nitazoxanide is a first-line, standard treatment for illness caused by C. parvum or G. lamblia infection in healthy (not immunosuppressed) adults and children and may also be considered in the treatment of illnesses caused by other protozoa or helminths [A31976].  Recently, this drug has been studied as a broad-spectrum antiviral agent due to its ability to inhibit the replication of several RNA and DNA viruses [A31977].', '["P01AX11"]', '[]');
