INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23825, 'Nplate 250mcg vial', 'انبليت 250مكجم فيال', '11,959', '8650', 'Idiopathic thrombocytopenic purpura (itp)', 'Romiplostim', 'Amgen > multipharma', 'Vial', 'فيال', '250mcg', '1', '', 'romiplostim is a fusion protein analog of thrombopoietin a hormone that regulates platelet production. romiplostim is indicated as a potential treatment for chronic idiopathic (immune) thrombocytopenic purpura (itp). romiplostim treatment is generally adm', '', '', 3141, '2024-10-21', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Romiplostim is a thrombopoiesis stimulating dimer Fc-peptide fusion protein (peptibody) to increase platelet production through activation of the thrombopoietin receptor. The peptibody molecule has two identical single-chain subunits, each one is made up of 269 amino acid residues. Each subunit consists of an IgG1 Fc carrier domain that is covalently attached to a polypeptide sequence that contains two binding domains to interact with thrombopoietin receptor c-Mpl. Each domain consists of 14 amino acids. Interestingly, romiplostim''s amino acid sequence is not similar to that of endogenous thrombopoietin. Romiplostim is produced by recombinant DNA technology in Escherichia coli. FDA approved on August 22, 2008.', '["B02BX04"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23826, 'Nplate 500mcg vial (n/a yet)', 'انبليت 500مكجم فيال', '10,716', '', 'Idiopathic thrombocytopenic purpura (itp)', 'Romiplostim', 'Amgen > multipharma', 'Vial', 'فيال', '500mcg', '1', '', 'romiplostim is a fusion protein analog of thrombopoietin a hormone that regulates platelet production. romiplostim is indicated as a potential treatment for chronic idiopathic (immune) thrombocytopenic purpura (itp). romiplostim treatment is generally adm', '', '', 745, '2022-06-18', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Romiplostim is a thrombopoiesis stimulating dimer Fc-peptide fusion protein (peptibody) to increase platelet production through activation of the thrombopoietin receptor. The peptibody molecule has two identical single-chain subunits, each one is made up of 269 amino acid residues. Each subunit consists of an IgG1 Fc carrier domain that is covalently attached to a polypeptide sequence that contains two binding domains to interact with thrombopoietin receptor c-Mpl. Each domain consists of 14 amino acids. Interestingly, romiplostim''s amino acid sequence is not similar to that of endogenous thrombopoietin. Romiplostim is produced by recombinant DNA technology in Escherichia coli. FDA approved on August 22, 2008.', '["B02BX04"]', '[]');
