INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24141, 'Ophthalin 1% intraocular pref. syringe', 'اوفثالين 1% سرنجة معبأة للعين', '160', '', '', 'Hyaluronic acid', 'Hyaltech ltd-united kingdom > medi tec-egypt', 'Syringe', 'حقنة', '1%', '1', '', 'indications is indicated for use as a surgical aid in the following ophthalmic surgicalprocedures: ï- cataract surgery with an intraocular lens ï- cataract surgery without an intraocular lens ï- secondary intraocular lens implantation ï- corneal transplan', '', '', 838, '2022-10-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Hyaluronic acid (HA) is an anionic, nonsulfated glycosaminoglycan found in connective, epithelial, and neural tissues; it was first isolated in 1934. Karl Meyer and John Palmer obtained glycosaminoglycan (GAG) from the bovine eye, giving it the name “hyaluronic acid”. HA is involved in many important physiological processes, including but not limited to wound healing, tissue regeneration, and joint lubrication. It demonstrates unique viscoelasticity, moisturizing, anti-inflammatory qualities, and other important properties that prove beneficial in various clinical applications.[A230128]  

HA is used in drug delivery systems for the treatment of cancer, ophthalmological conditions, joint conditions, and aesthetic imperfections.[A230133] Several preparations of hyaluronic acid have been approved by the FDA and are available in oral, topical, and injectable forms. A popular use of hyaluronic acid in recent years is cosmetic injection due to its ability to minimize the appearance of wrinkles and aging-related skin imperfections.[L32193]', '["S01KA51", "S01KA01", "D03AX05", "M09AX01", "R01AX09"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24143, 'Opicroceed 50 mg 10 f.c. tab.', 'اوبيكروسيد 50مجم 10 قرص', '8', '', 'Diabetes', 'Acarbose', 'El-obour', 'Tab', 'أقراص', '50 mg', '1', '', 'description an inhibitor of alpha glucosidase that retards the digestion and absorption of carbohydrates in the small intestine and hence reduces the increase in blood-glucose concentrations after a carbohydrate load. it is given orally to non-insulin dep', '', '', 1263, '2022-07-07', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Acarbose is a complex oligosaccharide that acts as an inhibitor of several enzymes responsible for the breakdown of complex carbohydrates in the intestines. It inhibits both pancreatic alpha-amylase and membrane-bound alpha-glucosidases - including intestinal glucoamylase, sucrase, maltase, and isomaltase - which are responsible for the metabolism of complex starches and oligo-, tri-, and disaccharides into absorbable simple sugars.[L31633,A37868] By inhibiting the activity of these enzymes, acarbose limits the absorption of dietary carbohydrates and the subsequent postprandial increase in blood glucose and insulin levels. Acarbose is therefore used in conjunction with diet, exercise, and other pharmacotherapies for the management of blood sugar levels in patients with type 2 diabetes.[L31628,L31633]

Acarbose is one of only two approved alpha-glucosidase inhibitors (the other being [miglitol]), receiving its first FDA approval in 1995 under the brand name Precose (since discontinued).[L31668] This class of antidiabetic therapy is not widely used due to their relatively modest impact on A1c, their requirement for thrice-daily dosing, and the potential for significant gastrointestinal adverse effects.[L31668]', '["A10BD17", "A10BF01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24144, 'Opikast 10 mg 14 scored f.c. tabs.', 'اوبيكاست 10مجم 14 قرص', '22', '', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'El-obour', 'Tab', 'أقراص', '10 mg', '2', 'علاج للربو', 'about montelukast leukotriene(cyslt1) receptor antagonist antiinflammatory antiasthma. mechanism of action of montelukast montelukast is competitively and selectively antagonizes cyslt1 receptor (leukotrienes receptor) mediated bronchoconstriction.montelu', '', '', 1460, '2022-10-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Montelukast was first approved for clinical use by the US FDA in 1998 as Merck''s brand name Singulair.[L6301] The medication is a member of the leukotriene receptor antagonist (LTRA) category of drugs.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Although capable of demonstrating effectiveness, the use of such LTRAs like montelukast is typically in addition to or complementary with the use of inhaled corticosteroids or other agents in asthma step therapy.[A178645] Regardless, in 2008-2009, there were FDA-led investigations into the possibility of montelukast to elicit neuropsychiatric effects like agitation, hallucinations, suicidal behaviour, and others in individuals who used the medication.[A178651] And although these kinds of effects are currently included in the official prescribing information for montelukast,[L6301,L6304,L6307,L6310,L6325,L6328,L6331] the drug still sees extensive use worldwide via millions of prescriptions annually and has since become available as a generic and as a brand name product.', '["R03DC03", "R03DC53"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24145, 'Opikast 4 mg 21 chew. tabs.', 'اوبيكاست 4مجم 21 اقراص مضغ', '33', '', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'El-obour', 'Tab', 'أقراص', '4 mg', '3', '', 'about montelukast leukotriene(cyslt1) receptor antagonist antiinflammatory antiasthma. mechanism of action of montelukast montelukast is competitively and selectively antagonizes cyslt1 receptor (leukotrienes receptor) mediated bronchoconstriction.montelu', '', '', 1182, '2022-06-17', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Montelukast was first approved for clinical use by the US FDA in 1998 as Merck''s brand name Singulair.[L6301] The medication is a member of the leukotriene receptor antagonist (LTRA) category of drugs.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Although capable of demonstrating effectiveness, the use of such LTRAs like montelukast is typically in addition to or complementary with the use of inhaled corticosteroids or other agents in asthma step therapy.[A178645] Regardless, in 2008-2009, there were FDA-led investigations into the possibility of montelukast to elicit neuropsychiatric effects like agitation, hallucinations, suicidal behaviour, and others in individuals who used the medication.[A178651] And although these kinds of effects are currently included in the official prescribing information for montelukast,[L6301,L6304,L6307,L6310,L6325,L6328,L6331] the drug still sees extensive use worldwide via millions of prescriptions annually and has since become available as a generic and as a brand name product.', '["R03DC03", "R03DC53"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24146, 'Opinidazole 1000mg 8 f.c.tab.', 'اوبينيدازول 1000مجم 8 اقراص', '56', '43.25', 'Antiprotozoal', 'Secnidazole', 'El-obour', 'Tab', 'أقراص', '1000mg', '2', 'مضاد للبكتيريا', 'about secnidazole a nitroimidazole derivative antiprotozoal. mechanism of action of secnidazole secnidazole is a nitro imidazole which has broad spectrum cidal activity against protozoa and some anaerobic bacteria. its selective toxicity to anaerobic micr', '6223004600234', '', 2369, '2023-12-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Secnidazole is a second-generation 5-nitroimidazole antimicrobial agent. It is structurally related to other 5-nitroimidazoles, including [DB00916] and [DB00911], but displays improved oral absorption and a longer terminal elimination half-life than other drugs in this class. Secnidazole is selective against many anaerobic Gram-positive and Gram-negative bacteria as well as protozoa.[A27210] Once it enters bacteria and parasites, secnidazole is activated by bacterial or parasitic enzymes to form a radical anion, thereby damaging and killing the target pathogen.[A245503]

Secnidazole has been available in many other countries in Europe, Asia, South America, and Africa for decades.[A245503, A245508] In September 2017, FDA approved secnidazole under the market name Solosec for the treatment of trichomoniasis and bacterial vaginosis.[L39524]', '["P01AB07", "J01RA07"]', '[]');
