INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24177, 'Optiray 300mg/ml i.v. 12 vial 100 ml', 'اوبتيراي 300مجم/مل 12 امبول 100مل حقن وريدي', '2,436', '', 'Radiographic procedures.contrast agent', 'Ioversol', 'Mallinckrodt inc. > ghalioungui trading', 'Vial', 'فيال', '300mg', '12', '', 'ioversol (inn) is an organoiodine compound that used as a contrast medium. it features both a high iodine content as well as several hydrophilic groups.', '', '', 721, '2022-07-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Ioversol is a non-ionic compound with a tri-iodinated benzene ring used as a contrast dye in diagnostic procedures to visualize different types of organs and tissues.[L41780] Iodine has a high atomic density, which gives it the ability to attenuate X-rays.[L41790] The intravascular administration of iodine compounds, such as ioversol, enhances the contrast between vessels in the path of the flow of the contrast medium and normal tissue, allowing the visualization of internal structures.[L41780] Ioversol is a highly hydrophilic agent considered to be generally safe[L41790]; however, serious adverse reactions have been reported due to the inadvertent intrathecal administration of ioversol, which is only indicated for intra-arterial and intravenous use.[L41780]

Ioversol was approved by the FDA in 1989 and is currently indicated for computed tomographic (CT) imaging and contrast enhancement in peripheral arteriography, coronary arteriography, and left ventriculography.[L41780,L41790]', '["V08AB07"]', '[]');
