INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24473, 'Pamidin 250mg 20 tab.', 'باميدين 250مجم 20 اقراص', '4', '', 'Anti-diabetic.secretagogues.sulfonylurea', 'Chlorpropamide', 'Kahira', 'Tab', 'أقراص', '250mg', '2', '', 'about chlorpropamide antidiabetic agent sulfonylurea derivative oral hypoglycemic agent for type ? ii diabetes. mechanism of action of chlorpropamide the drug exerts it`s action by increasing insulin release from the pancreas and by improving glucose tole', '6221068000120', '', 773, '2023-05-04', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Chlorpropamide is an oral antihyperglycemic agent used for the treatment of non-insulin-dependent diabetes mellitus (NIDDM). It belongs to the sulfonylurea class of insulin secretagogues, which act by stimulating β cells of the pancreas to release insulin. Sulfonylureas increase both basal insulin secretion and meal-stimulated insulin release. Medications in this class differ in their dose, rate of absorption, duration of action, route of elimination and binding site on their target pancreatic β cell receptor. Sulfonylureas also increase peripheral glucose utilization, decrease hepatic gluconeogenesis and may increase the number and sensitivity of insulin receptors. Sulfonylureas are associated with weight gain, though less so than insulin. Due to their mechanism of action, sulfonylureas may cause hypoglycemia and require consistent food intake to decrease this risk. The risk of hypoglycemia is increased in elderly, debilitated and malnourished individuals. Chlorpropamide is not recommended for the treatment of NIDDM as it increases blood pressure 