INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25397, 'Prostavasin 20mcg amp', 'بروستافازين 20مكجم امبولات', '165', '', 'Prostaglandin e1', 'Alprostadil', 'Schwarz > amc', 'Amp', 'أمبول', '20mcg', '1', 'يستخدم آلبروستاديل لعلاج ضعف الانت -- صاب الذي سببه عصبي او نفسي أو أسباب أخرى كما يستعمل في المواليد الجدد الذين لديهم عيوب خلقية في القلب .', '', '', '', 4121, '2022-07-24', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Alprostadil is produced endogenously and causes vasodilation by means of a direct effect on vascular and ductus arteriosus (DA) smooth muscle, preventing or reversing the functional closure of the DA that occurs shortly after birth. This results in increased pulmonary or systemic blood flow in infants. In infants, it is used for palliative, not definitive, therapy to temporarily maintain the patency of the ductus arteriosus until corrective or palliative surgery can be performed in neonates who have congenital heart defects and who depend upon the patent ductus for survival. In adults, it is used for the treatment of erectile dysfunction due to neurogenic, vasculogenic, psychogenic, or mixed etiology.', '["C01EA01", "G04BE01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25398, 'Prostavone 30 caps.', 'بروستافون 30 كبسولة', '26', '', 'Prostate support', 'Pumpkin seed oil+saw palmetto oil+zinc+nigella sativa', 'Safe pharma > biopharm egypt', 'Cap', 'كبسولة', '', '3', 'علاج التهابات البروستاتا -- تقوية البروستاتا وتحسين وظائفها', 'helps in management of benign prostate hyperplasia and prostatitis.', '6221126000055', '', 3271, '2023-02-26', 0, NULL, NULL, NULL, NULL, 0, 0, 0, NULL, NULL, NULL);
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25399, 'Prostec 5 mg 14 f.c.tab.', 'بروستك 5 مجم 14 قرص', '47', '', '5 alpha-reductase inhibitor', 'Finasteride', 'Sigma', 'Tab', 'أقراص', '5 mg', '2', 'علاج امراض الجهاز البولي والتناسلي -- تقليل الالتهابات', 'about finasteride 5 alpha-reductase inhibitor synthetic anti-androgen. mechanism of action of finasteride this synthetic 4-azasteroid derivative competitively inhibits steroid 5alpha reductase which is an enzyme responsible for active androgen dehydrotest', '6221051010129', '', 1408, '2022-12-08', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Finasteride is a synthetic 4-azasteroid compound [L10565] and specific inhibitor of steroid Type II 5α-reductase, which is an intracellular enzyme that converts the androgen testosterone into 5α-dihydrotestosterone (DHT). It works in a similar fashion as [dutasteride], which is another 5-alpha-reductase inhibitor, by exerting antiandrogenic effects. Finasteride is an orally active drug that was first approved by the FDA in 1992 for the treatment of benign prostatic hyperplasia to improve symptoms and reduce the risk for acute urinary retention or the need for surgical procedures.[L6244,L10565] In 1998, it was approved by the FDA to treat male pattern hair loss.[L6244] Finasteride is commonly marketed under the brand names Propecia and Proscar to be used aloneo or in combination with [doxazosin], an alpha-blocker.

Both benign prostatic hyperplasia and androgenic alopecia are androgen-dependent disorders that are characterized by _in situ_ high levels of DHT.[A178159] In the treatment of benign prostate hyperplasia, alpha-blockers such as [tamsulosin] and [terazosin] are also used. Compared to alpha-blockers that focus on providing the rapid relief of symptoms, 5α-reductase inhibitors aim to target the underlying disease by blocking the effects of the primary androgen involved in benign prostate hyperplasia and androgenic alopecia, thus reducing the risk for secondary complications while providing symptom control.[A2132]', '["D11AX10", "G04CA51", "G04CB01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25400, 'Prostetrol 10mg m.r. 30 tab.', 'بروستيترول 10مجم 30 اقراص', '65', '45', 'Antihypertensive.selective alpha 1 receptor blockers', 'Alfuzosin', 'Eva pharma', 'Tab', 'أقراص', '10mg', '3', 'علاج مرض ارتفاع ضغط الدم', 'description alfuzosin (inn provided as the hydrochloride salt) is an alpha-adrenergic blocker used to treat benign prostatic hyperplasia indication : for the reduction of urinary obstruction and relief of associated manifestations (eg. sensation of incomp', '6223002453801', '', 2165, '2024-03-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Benign prostatic hyperplasia (BPH) refers to a benign growth or hyperplasia of the prostate and leads to lower urinary tract symptoms in men, such as urgency, frequency and changes to urine flow. The prevalence of BPH is as high as 50%-60% for males in their 60''s, and this prevalence increases to 80%-90% of those over 70.[A228483] Alfuzosin is an alpha-1 adrenergic blocker used in the symptomatic treatment of BPH that works by relaxing the muscles in the prostate and bladder neck.[L31593] It was initially approved by the FDA in 2003 and is marketed by several pharmaceutical companies.[L9251]', '["G04CA01", "G04CA51"]', '[]');
