INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25853, 'Remeron soltab 30mg orodisp.tabs.', 'ريميرون سولتاب 30مجم اقراص', '40', '', 'Psychiatric.antidepressants', 'Mirtazapine', 'Cima technology inc.-usa > schering plough', 'Tab', 'أقراص', '30mg', '3', '', 'about mirtazapine tetracyclic antidepressant piperazino-azepine group antidepressant. mechanism of action of mirtazapine mirtazapine antagonizes alpha 2 adrenergic receptors 5ht2 and 5ht3 receptors. this will enhance noradrenergic and serotonergic activit', '', '', 1116, '2022-07-17', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Mirtazapine is a tetracyclic _piperazino-azepine_ antidepressant agent that was initially approved for the treatment of major depressive disorder (MDD) in the Netherlands in 1994.[A177946] This drug was first manufactured by Organon Inc., and received FDA approval in 1997 for the treatment of major depressive disorder.[T595, L6157] The effects of this drug may be observed as early as 1 week after beginning therapy.[A178144,L6160]

In addition to its beneficial effects in depression, mirtazapine has been reported to be efficacious in the off-label management of various other conditions.  It may improve the symptoms of neurological disorders, reverse weight loss caused by medical conditions, improve sleep, and prevent nausea and vomiting after surgery.[A177811]', '["N06AX11"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25854, 'Remicade 100 mg pd. vial for i.v. inf. 10 ml', 'ريميكاد 100مجم فيال 10 مل للتسريب الوريدي', '5,936', '5565', 'Tumor necrosis factor', 'Infliximab', 'Janssen cilag > ramco', 'Vial', 'فيال', '100 mg', '1', '', 'infliximab is a chimeric monoclonal antibody biologic drug that works against tumor necrosis factor alpha (tnf-alpha) and is used to treat autoimmune diseases. infliximab was approved by the u.s. food and drug administration (fda) for the treatment of cro', '', '', 5404, '2025-07-17', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Infliximab is a tumor necrosis factor (TNF-alpha or TNF-α) blocker and a chimeric monoclonal IgG1 antibody composed of human constant (75%) and murine variable (25%) regions [A31469]. Infliximab is produced by a recombinant cell line cultured by continuous perfusion. Tumor necrosis factor-alpha (TNF-α) is a key proinflammatory cytokine involved in chronic inflammatory diseases [A31469]. Its hyperactivity and enhanced signalling pathways can be observed in inflammatory diseases where it activates further pro-inflammatory cascades. By binding to both the soluble subunit and the membrane-bound precursor of TNF-α [A106], infliximab disrupts the interaction of TNF-α with its receptors and may also cause lysis of cells that produce TNF-α [A106].

Infliximab was first approved by the FDA in 1998 under the market name Remicade as an intravenous injection. It is indicated for the treatment of various inflammatory disorders such as adult or pediatric Chron''s disease, adult or pediatric ulcerative colitis, rheumatoid arthritis in combination with methotrexate, ankylosing spondyliti, psoriatic arthritis, and plaque psoriasis [FDA Label]. In clinical trials, multiple infusions of infliximab displayed in a reduction of signs and symptoms of inflammatory diseases and induction of remission in patients who have had an inadequate response to alternative first-line therapies for that disorder [FDA Label]. 

There are currently two biosilimars of infliximab available in the US market that demonstrate a high degree of similarity to the reference product, Remicade. They are approved for all eligible indications of the reference product. Inflectra, a first biosimilar drug product, was approved in 2016. In December 2017, Ixifi, a second biosimilar that was developed by Pfizer, was granted approval by the FDA.', '["L04AB02"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25855, 'Remigoline 0.5mg 2 tab.', 'ريميجولين 0.5 مجم 2 قرص', '104', '72', 'Dopamine agonist and prolactin inhibitor', 'Cabergoline', 'Hochster pharmaceutical industries', 'Tab', 'أقراص', '0.5mg', '1', 'تنشيف اللبن بعد الفطام -- علاج ارتفاع هرمون البرولاكتين -- علاج تاخر الحمل', '', '6224007751244', '', 2579, '2024-08-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Cabergoline, an ergot derivative, is a long-acting dopamine agonist and prolactin inhibitor. It is used to treat hyperprolactinemic disorders and Parkinsonian Syndrome. Cabergoline possesses potent agonist activity on dopamine D2 receptors.', '["N04BC06", "G02CB03"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25856, 'Remikabiotec 500mg/2ml vial', 'ريميكابيوتك 500مجم/2مل امبول', '12', '', 'Aminoglycoside antibiotic', 'Amikacin', 'Badr pharma > jewels pharm co.', 'Vial', 'فيال', '500mg', '1', '', 'description amikacin is a semi-synthetic aminoglycoside antibiotic derived from kanamycin a. similar to other aminoglycosides amikacin disrupts bacterial protein synthesis by binding to the 30s ribosome of susceptible organisms. binding interferes with mr', '', '', 692, '2022-07-18', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Amikacin is a semi-synthetic aminoglycoside antibiotic that is derived from kanamycin A.[FDA label] Amikacin is synthesized by acylation with the l-(-)-γ-amino-α-hydroxybutyryl side chain at the C-1 amino group of the deoxystreptamine moiety of kanamycin A.[A39531]

Amikacin''s unique property is that it exerts activity against more resistant gram-negative bacilli such as Acinetobacter baumanii and Pseudomonas aeruginosa. Amikacin also exerts excellent activity against most aerobic gram-negative bacilli from the Enterobacteriaceae family, including Nocardia and some Mycobacterium (M. avium-intracellulare, M. chelonae, and M. fortuitum)[L4680]. M. avium-intracellulare (MAC) is a type of nontuberculous mycobacteria (NTM) found in water and soil. Symptoms of this disease include a persistent cough, fatigue, weight loss, night sweats, and shortness of breath and the coughing up of blood.[L4680]

Several forms of amikacin are used currently, including an intravenous (IV) or intramuscular (IM) injection.[F1949] In September 2018, a liposomal inhalation suspension of this drug was approved by the FDA for the treatment of lung disease caused by Mycobacterium avium complex (MAC) bacteria in a small population of patients with the disease who do not respond to traditional treatment.[L4680,L4681]', '["S01AA21", "D06AX12", "J01GB06", "J01RA06"]', '[]');
