INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25971, 'Revlimid 10mg 21 caps. ss', 'ريفليميد 10مجم 21 كبسولة', '123,626', '', 'Immunosuppressants', 'Lenalidomide', 'Celgene corporation > egyptian pharmaceutical trading companyegypt', 'Cap', 'كبسولة', '10mg', '3', 'مثبط / كابح للمناعة في عمليات زرع الأعضاء مثل زراعة الكلى وغيرها', 'thalidomide analog; immunomodulatory and antiangiogenic; inhibits secretion of proinflammatory cytokines; enhances cell-mediated immunity by stimulating proliferation of anti-cd3 stimulated t cells', '', '', 1842, '2022-10-19', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Lenalidomide (previously referred to as CC-5013) is an immunomodulatory drug with potent antineoplastic, anti-angiogenic, and anti-inflammatory properties.[A228553] It is a 4-amino-glutamyl analogue of [thalidomide] [A228543] and like thalidomide, lenalidomide exists as a racemic mixture of the active S(-) and R(+) forms.[A228708] However, lenalidomide is much safer and potent than thalidomide, with fewer adverse effects and toxicities.[A714, A228543] Thalidomide and its analogues, including lenalidomide, are referred to as immunomodulatory imide drugs (also known as cereblon modulators), which are a class of immunomodulatory drugs that contain an imide group. Lenalidomide works through various mechanisms of actions that promote malignant cell death and enhance host immunity.[A228708] Available as oral capsules, lenalidomide is approved by the FDA and EU for the treatment of multiple myeloma, myelodysplastic syndromes, mantle cell lymphoma, follicular lymphoma, and marginal zone lymphoma in selected patients.[L16028] Due to severe teratogenicity, pregnancy must be excluded before the start of treatment and patients must enroll in the lenalidomide Risk Evaluation and Mitigation Strategy (REMS) program to ensure contraception adherence.[A228708]', '["L04AX04"]', '[]');
