INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26241, 'Rofcox 12.5mg/5ml 120ml susp.', 'روفكوكس 12.5مجم/5مل 120مل شراب معلق', '23', '', 'Nsaid.selective cox-2 inhibitors', 'Rofecoxib', 'Sigma', 'Susp', 'معلق', '12.5mg', '1', '', 'about rofecoxib nsaid selective cox-2 inhibitor analgesic anti-inflammatory. mechanism of action of rofecoxib rofecoxib inhibits prostaglandin synthesis by inhibiting cox2 enzyme. this nsaid inhibit cox2 enzyme selectively (cox-2 is only produced in respo', '', '', 802, '2022-08-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Rofecoxib is used for the treatment of osteoarthritis, rheumatoid arthritis, acute pain in adults, and primary dysmenorrhea, as well as acute treatment of migraine attacks with or without auras. Rofecoxib is a solid. This compound belongs to the stilbenes. These are organic compounds containing a 1,2-diphenylethylene moiety. Stilbenes (C6-C2-C6 ) are derived from the common phenylpropene (C6-C3) skeleton building block. The introduction of one or more hydroxyl groups to a phenyl ring lead to stilbenoids. Rofecoxib has a half-life of 17 hours and its mean oral bioavailability at therapeutically recommended doses of 125, 25, and 50 mg is approximately 93%. The proteins that rofecoxib target include elastin and prostaglandin G/H synthase 2. Cytochrome P450 1A2, Cytochrome P450 3A4, Cytochrome P450 2C9, Cytochrome P450 2C8, and Prostaglandin G/H synthase 1 are known to metabolize rofecoxib. On September 30, 2004, Merck voluntarily withdrew rofecoxib from the market because of concerns about increased risk of heart attack and stroke associated with long-term, high-dosage use.', '["M01AH02"]', '[]');
